rdfOntologyExtensions.exampleOntology:
@prefix deploy: <https://ggen.example.com/deploy/> .
@prefix k8s: <https://ggen.example.com/kubernetes/> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .

deploy:api-service a deploy:ContainerStrategy ;
  deploy:baseImage "node:20-alpine" ;
  deploy:multistage true ;
  deploy:layers (
    deploy:builder-layer
    deploy:production-layer
  ) ;
  deploy:targetPlatforms ( "linux/amd64" "linux/arm64" ) .

deploy:api-k8s-deployment a k8s:Deployment ;
  k8s:replicas 3 ;
  k8s:strategy "RollingUpdate" ;
  k8s:resources [
    k8s:requestsCpu "100m" ;
    k8s:requestsMemory "128Mi" ;
    k8s:limitsCpu "500m" ;
    k8s:limitsMemory "512Mi"
  ] ;
  k8s:probes [
    k8s:livenessPath "/health" ;
    k8s:readinessPath "/ready" ;
    k8s:startupPath "/startup"
  ] .

deploy:api-helm-chart a k8s:HelmChart ;
  k8s:chartName "ggen-api" ;
  k8s:chartVersion "1.0.0" ;
  k8s:values [
    k8s:replicaCount 3 ;
    k8s:autoscaling true ;
    k8s:ingress true
  ] .

helmChart.templates/deployment.yaml:
{{- $fullName := include "ggen-api.fullname" . -}}
apiVersion: apps/v1
kind: Deployment
metadata:
  name: {{ $fullName }}
  labels:
    {{- include "ggen-api.labels" . | nindent 4 }}
spec:
  {{- if not .Values.autoscaling.enabled }}
  replicas: {{ .Values.replicaCount }}
  {{- end }}
  selector:
    matchLabels:
      {{- include "ggen-api.selectorLabels" . | nindent 6 }}
  template:
    metadata:
      annotations:
        {{- toYaml .Values.podAnnotations | nindent 8 }}
      labels:
        {{- include "ggen-api.selectorLabels" . | nindent 8 }}
    spec:
      {{- with .Values.imagePullSecrets }}
      imagePullSecrets:
        {{- toYaml . | nindent 8 }}
      {{- end }}
      serviceAccountName: {{ include "ggen-api.serviceAccountName" . }}
      securityContext:
        {{- toYaml .Values.podSecurityContext | nindent 8 }}
      containers:
      - name: {{ .Chart.Name }}
        securityContext:
          {{- toYaml .Values.securityContext | nindent 12 }}
        image: "{{ .Values.image.repository }}:{{ .Values.image.tag | default .Chart.AppVersion }}"
        imagePullPolicy: {{ .Values.image.pullPolicy }}
        ports:
        - name: http
          containerPort: 3000
          protocol: TCP
        {{- with .Values.env }}
        env:
          {{- toYaml . | nindent 10 }}
        {{- end }}
        {{- with .Values.envFrom }}
        envFrom:
          {{- toYaml . | nindent 10 }}
        {{- end }}
        livenessProbe:
          {{- toYaml .Values.livenessProbe | nindent 10 }}
        readinessProbe:
          {{- toYaml .Values.readinessProbe | nindent 10 }}
        resources:
          {{- toYaml .Values.resources | nindent 10 }}
      {{- with .Values.nodeSelector }}
      nodeSelector:
        {{- toYaml . | nindent 8 }}
      {{- end }}
      {{- with .Values.affinity }}
      affinity:
        {{- toYaml . | nindent 8 }}
      {{- end }}
      {{- with .Values.tolerations }}
      tolerations:
        {{- toYaml . | nindent 8 }}
      {{- end }}

