@prefix infra: <https://ggen.example.com/infrastructure/> .
@prefix cloud: <https://ggen.example.com/cloud/> .
@prefix network: <https://ggen.example.com/network/> .

infra:production-env a infra:Infrastructure ;
  infra:provider "aws" ;
  infra:region "us-east-1" ;
  infra:resources (
    network:production-vpc
    cloud:api-cluster
    cloud:postgres-rds
    cloud:redis-cache
  ) .

network:production-vpc a network:VPC ;
  network:cidrBlock "10.0.0.0/16" ;
  network:subnets (
    [ network:name "public-1" ; network:cidr "10.0.1.0/24" ; network:az "us-east-1a" ]
    [ network:name "public-2" ; network:cidr "10.0.2.0/24" ; network:az "us-east-1b" ]
    [ network:name "private-1" ; network:cidr "10.0.11.0/24" ; network:az "us-east-1a" ]
    [ network:name "private-2" ; network:cidr "10.0.12.0/24" ; network:az "us-east-1b" ]
  ) ;
  network:internetGateway true ;
  network:natGateways 2 .

cloud:api-cluster a cloud:EKSCluster ;
  cloud:version "1.28" ;
  cloud:nodeGroups [
    cloud:name "general" ;
    cloud:instanceTypes ( "t3.medium" "t3.large" ) ;
    cloud:minSize 3 ;
    cloud:maxSize 10 ;
    cloud:desiredSize 3
  ] .

cloud:postgres-rds a cloud:DatabaseCluster ;
  cloud:engine "postgres" ;
  cloud:version "15.4" ;
  cloud:instanceClass "db.r6g.large" ;
  cloud:storageType "gp3" ;
  cloud:allocatedStorage 100 ;
  cloud:multiAz true .

terraform.main.tf:
# Terraform configuration generated from RDF ontology
# Source: ontology/infrastructure.ttl (infra:production-env)

terraform {
  required_version = ">= 1.6"

  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 5.0"
    }
  }

  backend "s3" {
    bucket         = "ggen-terraform-state"
    key            = "production/terraform.tfstate"
    region         = "us-east-1"
    encrypt        = true
    dynamodb_table = "ggen-terraform-locks"
  }
}

provider "aws" {
  region = var.aws_region

  default_tags {
    tags = {
      Environment = "production"
      ManagedBy   = "terraform"
      Project     = "ggen-api"
      GeneratedFrom = "ontology/infrastructure.ttl"
    }
  }
}

module "vpc" {
  source = "./modules/vpc"

  cidr_block = "10.0.0.0/16"

  public_subnets = [
    { cidr = "10.0.1.0/24", az = "us-east-1a" },
    { cidr = "10.0.2.0/24", az = "us-east-1b" }
  ]

  private_subnets = [
    { cidr = "10.0.11.0/24", az = "us-east-1a" },
    { cidr = "10.0.12.0/24", az = "us-east-1b" }
  ]

  enable_nat_gateway = true
  nat_gateway_count  = 2
}

module "eks" {
  source = "./modules/eks"

  cluster_name    = "ggen-api-cluster"
  cluster_version = "1.28"

  vpc_id     = module.vpc.vpc_id
  subnet_ids = module.vpc.private_subnet_ids

  node_groups = {
    general = {
      desired_size = 3
      min_size     = 3
      max_size     = 10

      instance_types = ["t3.medium", "t3.large"]
      capacity_type  = "ON_DEMAND"

      labels = {
        workload = "general"
      }

      taints = []
    }
  }
}

module "rds" {
  source = "./modules/rds"

  identifier = "ggen-postgres"
  engine     = "postgres"
  engine_version = "15.4"

  instance_class    = "db.r6g.large"
  allocated_storage = 100
  storage_type      = "gp3"
  storage_encrypted = true

  multi_az = true

  vpc_id     = module.vpc.vpc_id
  subnet_ids = module.vpc.private_subnet_ids

  backup_retention_period = 7
  backup_window          = "03:00-04:00"
  maintenance_window     = "mon:04:00-mon:05:00"
}

module "elasticache" {
  source = "./modules/elasticache"

  cluster_id = "ggen-redis"
  engine     = "redis"
  engine_version = "7.0"

  node_type       = "cache.r6g.large"
  num_cache_nodes = 2

  vpc_id     = module.vpc.vpc_id
  subnet_ids = module.vpc.private_subnet_ids
}

