@prefix mon: <https://ggen.example.com/monitoring/> .
@prefix metric: <https://ggen.example.com/metrics/> .
@prefix trace: <https://ggen.example.com/tracing/> .

metric:api-latency a mon:MetricDefinition ;
  mon:name "http_request_duration_seconds" ;
  mon:type "histogram" ;
  mon:labels ( "method" "route" "status" ) ;
  mon:scrapeInterval "15s" ;
  mon:buckets ( 0.005 0.01 0.025 0.05 0.1 0.25 0.5 1.0 2.5 5.0 10.0 ) .

mon:high-latency-alert a mon:AlertRule ;
  mon:name "HighAPILatency" ;
  mon:condition "histogram_quantile(0.95, rate(http_request_duration_seconds_bucket[5m])) > 1.0" ;
  mon:severity "warning" ;
  mon:duration "5m" ;
  mon:annotations [
    mon:summary "95th percentile latency above 1s" ;
    mon:description "API latency P95 is {{ $value }}s (threshold: 1s)"
  ] .

mon:api-dashboard a mon:Dashboard ;
  mon:title "ggen API Metrics" ;
  mon:panels (
    [ mon:type "graph" ; mon:title "Request Rate" ; mon:query "rate(http_requests_total[5m])" ]
    [ mon:type "graph" ; mon:title "Latency P95" ; mon:query "histogram_quantile(0.95, rate(http_request_duration_seconds_bucket[5m]))" ]
    [ mon:type "graph" ; mon:title "Error Rate" ; mon:query "rate(http_requests_total{status=~\"5..\"}[5m])" ]
  ) ;
  mon:datasource "Prometheus" ;
  mon:refreshInterval "30s" .

trace:otel-config a trace:TracingConfig ;
  trace:sampler "parentbased_traceidratio" ;
  trace:samplerArg "0.1" ;
  trace:exporters ( "jaeger" "otlp" ) ;
  trace:processors ( "batch" "memory_limiter" "resource" ) .

