@prefix gitops: <https://ggen.example.com/gitops/> .
@prefix cd: <https://ggen.example.com/continuous-delivery/> .

gitops:api-app a gitops:Application ;
  gitops:name "ggen-api" ;
  gitops:sourceRepo "https://github.com/org/ggen-gitops" ;
  gitops:targetRevision "main" ;
  gitops:path "apps/ggen-api" ;
  gitops:syncPolicy [
    gitops:automated true ;
    gitops:prune true ;
    gitops:selfHeal true ;
    gitops:syncOptions ( "CreateNamespace=true" )
  ] ;
  gitops:environments (
    gitops:staging-env
    gitops:production-env
  ) .

gitops:staging-env a gitops:Environment ;
  gitops:name "staging" ;
  gitops:cluster "https://staging-k8s.example.com" ;
  gitops:namespace "staging" ;
  gitops:values [
    gitops:replicaCount 2 ;
    gitops:resources [
      gitops:limitsCpu "500m" ;
      gitops:limitsMemory "512Mi"
    ]
  ] .

gitops:production-env a gitops:Environment ;
  gitops:name "production" ;
  gitops:cluster "https://prod-k8s.example.com" ;
  gitops:namespace "production" ;
  gitops:values [
    gitops:replicaCount 3 ;
    gitops:resources [
      gitops:limitsCpu "1000m" ;
      gitops:limitsMemory "1Gi"
    ]
  ] ;
  gitops:progressiveDelivery cd:canary-strategy .

cd:canary-strategy a cd:ProgressiveDelivery ;
  cd:strategy "canary" ;
  cd:steps (
    [ cd:weight 20 ; cd:pause "5m" ]
    [ cd:weight 50 ; cd:pause "10m" ]
    [ cd:weight 100 ]
  ) ;
  cd:analysis [
    cd:metrics ( "error-rate" "latency-p95" ) ;
    cd:threshold [ cd:errorRate "< 5%" ; cd:latencyP95 "< 500ms" ]
  ] ;
  cd:rollbackOn "analysis-failure" .

@prefix deploy: <https://ggen.example.com/deployment/> .
@prefix release: <https://ggen.example.com/release/> .
@prefix feature: <https://ggen.example.com/features/> .

deploy:api-canary a deploy:CanaryStrategy ;
  deploy:name "ggen-api-canary" ;
  deploy:steps (
    [ deploy:weight 10 ; deploy:pause "2m" ]
    [ deploy:weight 25 ; deploy:pause "5m" ]
    [ deploy:weight 50 ; deploy:pause "10m" ]
    [ deploy:weight 75 ; deploy:pause "5m" ]
    [ deploy:weight 100 ]
  ) ;
  deploy:metrics (
    [ deploy:name "request-success-rate" ; deploy:threshold ">= 99%" ]
    [ deploy:name "request-duration-p95" ; deploy:threshold "< 500ms" ]
    [ deploy:name "error-rate-5xx" ; deploy:threshold "< 1%" ]
  ) ;
  deploy:analysis [
    deploy:interval "1m" ;
    deploy:iterations 10 ;
    deploy:threshold 3
  ] ;
  deploy:rollbackThreshold 5 ;
  deploy:rollbackPolicy release:auto-rollback .

deploy:api-blue-green a deploy:BlueGreenStrategy ;
  deploy:name "ggen-api-blue-green" ;
  deploy:autoPromotion false ;
  deploy:autoPromotionDelay "5m" ;
  deploy:scaleDownDelay "10m" ;
  deploy:previewService "ggen-api-preview" ;
  deploy:activeService "ggen-api" .

feature:new-api-endpoint a feature:FeatureFlag ;
  feature:key "new_api_endpoint_v2" ;
  feature:enabled true ;
  feature:rolloutPercentage 25 ;
  feature:targeting [
    feature:users ( "beta-testers" ) ;
    feature:segments ( "internal" "premium" )
  ] ;
  feature:defaultVariation "control" ;
  feature:variations (
    [ feature:name "control" ; feature:value false ]
    [ feature:name "treatment" ; feature:value true ]
  ) .

release:auto-rollback a release:RollbackPolicy ;
  release:enabled true ;
  release:triggers (
    [ release:metric "error-rate" ; release:threshold "> 5%" ]
    [ release:metric "latency-p99" ; release:threshold "> 2s" ]
    [ release:metric "availability" ; release:threshold "< 99%" ]
  ) ;
  release:window "15m" ;
  release:notifications [
    release:slack "#deployments" ;
    release:pagerduty "deployment-team"
  ] .

