integrationExample.ggenToml:
# ggen.toml configuration for generating deployment artifacts

[graph]
ontology_paths = [
  "ontology/infrastructure.ttl",
  "ontology/deployment.ttl",
  "ontology/monitoring.ttl"
]

# Container Orchestration Rules
[[rules.inference]]
name = "materialize_k8s_resources"
query = '''
CONSTRUCT {
  ?deployment a k8s:Deployment ;
    k8s:replicas ?replicas ;
    k8s:image ?image ;
    k8s:resources ?resources .
}
WHERE {
  ?service a deploy:Service ;
    deploy:replicas ?replicas ;
    deploy:image ?image ;
    deploy:resources ?resources .
}
'''

[[rules.generation]]
name = "generate_k8s_deployment"
query = '''
SELECT ?name ?replicas ?image ?cpu ?memory
WHERE {
  ?deployment a k8s:Deployment ;
    k8s:name ?name ;
    k8s:replicas ?replicas ;
    k8s:image ?image ;
    k8s:resources [ k8s:cpu ?cpu ; k8s:memory ?memory ] .
}
'''
template = "templates/kubernetes/deployment.yaml.tera"
output = "generated/k8s/{{ name }}-deployment.yaml"

# Infrastructure as Code Rules
[[rules.generation]]
name = "generate_terraform"
query = '''
SELECT ?vpc ?region ?cidr ?subnets
WHERE {
  ?infra a infra:Infrastructure ;
    infra:vpc ?vpc ;
    infra:region ?region ;
    infra:cidr ?cidr ;
    infra:subnets ?subnets .
}
'''
template = "templates/terraform/main.tf.tera"
output = "generated/terraform/main.tf"

# Monitoring Rules
[[rules.generation]]
name = "generate_prometheus_alerts"
query = '''
SELECT ?name ?condition ?severity ?duration
WHERE {
  ?alert a mon:AlertRule ;
    mon:name ?name ;
    mon:condition ?condition ;
    mon:severity ?severity ;
    mon:duration ?duration .
}
'''
template = "templates/prometheus/alerts.yml.tera"
output = "generated/monitoring/alerts.yml"

# GitOps Rules
[[rules.generation]]
name = "generate_argocd_app"
query = '''
SELECT ?name ?repo ?path ?namespace
WHERE {
  ?app a gitops:Application ;
    gitops:name ?name ;
    gitops:sourceRepo ?repo ;
    gitops:path ?path ;
    gitops:namespace ?namespace .
}
'''
template = "templates/argocd/application.yaml.tera"
output = "generated/argocd/{{ name }}.yaml"

# Canary Deployment Rules
[[rules.generation]]
name = "generate_rollout_strategy"
query = '''
SELECT ?name ?steps ?metrics
WHERE {
  ?strategy a deploy:CanaryStrategy ;
    deploy:name ?name ;
    deploy:steps ?steps ;
    deploy:metrics ?metrics .
}
'''
template = "templates/argo-rollouts/canary.yaml.tera"
output = "generated/rollouts/{{ name }}.yaml"
